class BasicDefaultParserState(Enum):
    LOOKING_FOR_START1 = 0
    LOOKING_FOR_START2 = 1
    GETTING_MSG_ID = 2
    GETTING_LENGTH = 3
    GETTING_PAYLOAD = 4


class BasicDefault:
    """
    BasicDefault - Frame format parser and encoder
    
    Format: [START_BYTE1=0x90] [START_BYTE2=0x71] [MSG_ID] [LEN] [MSG...] [CRC1] [CRC2]
    """

    START_BYTE1 = 0x90
    START_BYTE2 = 0x71
    HEADER_SIZE = 4
    FOOTER_SIZE = 2
    OVERHEAD = 6
    LENGTH_BYTES = 1

    def __init__(self, get_msg_length: Callable[[int], int] = None):
        """
        Initialize the BasicDefault parser
        
        Args:
            get_msg_length: Callback function to get message length from msg_id
                            Required for formats without a length field
        """
        self.get_msg_length = get_msg_length
        self.reset()

    def reset(self):
        """Reset parser state"""
        self.state = BasicDefaultParserState.LOOKING_FOR_START1
        self.buffer = []
        self.packet_size = 0
        self.msg_id = 0
        self.msg_length = 0

    def parse_byte(self, byte: int) -> FrameMsgInfo:
        """
        Parse a single byte
        
        Returns:
            FrameMsgInfo with valid=True when a complete valid message is received
        """
        result = FrameMsgInfo()

        if self.state == BasicDefaultParserState.LOOKING_FOR_START1:
            if byte == self.START_BYTE1:
                self.buffer = [byte]
                self.state = BasicDefaultParserState.LOOKING_FOR_START2

        elif self.state == BasicDefaultParserState.LOOKING_FOR_START2:
            if byte == self.START_BYTE2:
                self.buffer.append(byte)
                self.state = BasicDefaultParserState.GETTING_MSG_ID
            elif byte == self.START_BYTE1:
                self.buffer = [byte]
                self.state = BasicDefaultParserState.LOOKING_FOR_START2
            else:
                self.state = BasicDefaultParserState.LOOKING_FOR_START1

        elif self.state == BasicDefaultParserState.GETTING_MSG_ID:
            self.buffer.append(byte)
            self.msg_id = byte
            self.state = BasicDefaultParserState.GETTING_LENGTH

        elif self.state == BasicDefaultParserState.GETTING_LENGTH:
            self.buffer.append(byte)
            self.msg_length = byte
            self.packet_size = self.OVERHEAD + self.msg_length
            self.state = BasicDefaultParserState.GETTING_PAYLOAD

        elif self.state == BasicDefaultParserState.GETTING_PAYLOAD:
            self.buffer.append(byte)

