# TinyDefault Frame Format
# =============================================================================

class TinyDefaultParserState(Enum):
    LOOKING_FOR_START = 0
    GETTING_MSG_ID = 1
    GETTING_LENGTH = 2
    GETTING_PAYLOAD = 3


class TinyDefault:
    """
    TinyDefault - Frame format parser and encoder
    
    Format: [START_BYTE=0x71] [MSG_ID] [LEN] [MSG...] [CRC1] [CRC2]
    """

    START_BYTE = 0x71
    HEADER_SIZE = 3
    FOOTER_SIZE = 2
    OVERHEAD = 5
    LENGTH_BYTES = 1

    def __init__(self, get_msg_length: Callable[[int], int] = None):
        """
        Initialize the TinyDefault parser
        
        Args:
            get_msg_length: Callback function to get message length from msg_id
                            Required for formats without a length field
        """
        self.get_msg_length = get_msg_length
        self.reset()

    def reset(self):
        """Reset parser state"""
        self.state = TinyDefaultParserState.LOOKING_FOR_START
        self.buffer = []
        self.packet_size = 0
        self.msg_id = 0
        self.msg_length = 0

    def parse_byte(self, byte: int) -> FrameMsgInfo:
        """
        Parse a single byte
        
        Returns:
            FrameMsgInfo with valid=True when a complete valid message is received
        """
        result = FrameMsgInfo()

        if self.state == TinyDefaultParserState.LOOKING_FOR_START:
            if byte == self.START_BYTE:
                self.buffer = [byte]
                self.state = TinyDefaultParserState.GETTING_MSG_ID

        elif self.state == TinyDefaultParserState.GETTING_MSG_ID:
            self.buffer.append(byte)
            self.msg_id = byte
            self.state = TinyDefaultParserState.GETTING_LENGTH

        elif self.state == TinyDefaultParserState.GETTING_LENGTH:
            self.buffer.append(byte)
            self.msg_length = byte
            self.packet_size = self.OVERHEAD + self.msg_length
            self.state = TinyDefaultParserState.GETTING_PAYLOAD

        elif self.state == TinyDefaultParserState.GETTING_PAYLOAD:
            self.buffer.append(byte)

            if len(self.buffer) >= self.packet_size:
                # Validate checksum
                msg_length = self.packet_size - self.OVERHEAD
                ck = fletcher_checksum(self.buffer, 1, 1 + msg_length + 1 + 1)
                if ck[0] == self.buffer[-2] and ck[1] == self.buffer[-1]:
                    result.valid = True
                    result.msg_id = self.msg_id
                    result.msg_len = msg_length
                    result.msg_data = bytes(self.buffer[self.HEADER_SIZE:self.packet_size - self.FOOTER_SIZE])
                self.state = TinyDefaultParserState.LOOKING_FOR_START

        return result

    def encode(self, msg_id: int, msg: bytes) -> bytes:
        """
        Encode a message with TinyDefault format
        
        Args:
            msg_id: Message ID
            msg: Message data bytes
        
        Returns:
            Encoded frame as bytes
        """
        output = []
        output.append(self.START_BYTE)
        output.append(msg_id)
        output.append(len(msg) & 0xFF)
        output.extend(msg)
        ck = fletcher_checksum(output, 1, 1 + len(msg) + 1 + 1)
        output.append(ck[0])
        output.append(ck[1])
        return bytes(output)

    def encode_msg(self, msg) -> bytes:
        """Encode a message object (must have msg_id and pack() method)"""
        return self.encode(msg.msg_id, msg.pack())

    @staticmethod
    def validate_packet(buffer: Union[bytes, List[int]]) -> FrameMsgInfo:
        """
        Validate a complete TinyDefault packet in a buffer
        
        Args:
            buffer: Buffer containing the complete packet
        
        Returns:
            FrameMsgInfo with valid=True if packet is valid
        """
        result = FrameMsgInfo()

        if len(buffer) < TinyDefault.OVERHEAD:
            return result

        if buffer[0] != TinyDefault.START_BYTE:
            return result

        msg_length = len(buffer) - TinyDefault.OVERHEAD

        # Validate checksum
        ck = fletcher_checksum(buffer, 1, 1 + msg_length + 1 + 1)
        if ck[0] == buffer[-2] and ck[1] == buffer[-1]:
            result.valid = True
            result.msg_id = buffer[1]
            result.msg_len = msg_length
            result.msg_data = bytes(buffer[TinyDefault.HEADER_SIZE:len(buffer) - TinyDefault.FOOTER_SIZE])

        return result
