        if self.state == TinyExtendedLengthParserState.LOOKING_FOR_START:
            if byte == self.START_BYTE:
                self.buffer = [byte]
                self.state = TinyExtendedLengthParserState.GETTING_MSG_ID

        elif self.state == TinyExtendedLengthParserState.GETTING_MSG_ID:
            self.buffer.append(byte)
            self.msg_id = byte
            self.state = TinyExtendedLengthParserState.GETTING_LENGTH

        elif self.state == TinyExtendedLengthParserState.GETTING_LENGTH:
            self.buffer.append(byte)
            if len(self.buffer) == 3:
                self.length_lo = byte
            else:
                self.msg_length = self.length_lo | (byte << 8)
                self.packet_size = self.OVERHEAD + self.msg_length
                self.state = TinyExtendedLengthParserState.GETTING_PAYLOAD

        elif self.state == TinyExtendedLengthParserState.GETTING_PAYLOAD:
            self.buffer.append(byte)

            if len(self.buffer) >= self.packet_size:
                # Validate checksum
                msg_length = self.packet_size - self.OVERHEAD
                ck = fletcher_checksum(self.buffer, 1, 1 + msg_length + 1 + 2)
