        output.append(msg_id)
        output.append(len(msg) & 0xFF)
        output.append((len(msg) >> 8) & 0xFF)
        output.extend(msg)
        ck = fletcher_checksum(output, 1, 1 + len(msg) + 1 + 2)
        output.append(ck[0])
        output.append(ck[1])
        return bytes(output)

