class UbxFrame:
    """
    UbxFrame - Frame format parser and encoder
    
    Format: [SYNC1=0xB5] [SYNC2=0x62] [MSG_ID] [LEN] [MSG...] [CRC1] [CRC2]
    """

    START_BYTE1 = 0xB5
    START_BYTE2 = 0x62
    HEADER_SIZE = 5
    FOOTER_SIZE = 2
